                content: '<!-- Vertical timeline section -->\n<section class="timeline" aria-labelledby="timeline-heading">\n  <div class="container">\n    <h2 id="timeline-heading">Our journey</h2>\n    \n    <!-- Timeline items container with vertical line -->\n    <div class="timeline-items" role="list">\n      <!-- Individual timeline entry -->\n      <div class="timeline-item" role="listitem">\n        <div class="timeline-marker" aria-hidden="true"></div>\n        <div class="timeline-content">\n          <div class="timeline-date" aria-label="Year 2020">2020</div>\n          <h3>Founded</h3>\n          <p>Started with a vision to make web development accessible to everyone.</p>\n        </div>\n      </div>\n      \n      <div class="timeline-item" role="listitem">\n        <div class="timeline-marker" aria-hidden="true"></div>\n        <div class="timeline-content">\n          <div class="timeline-date" aria-label="Year 2021">2021</div>\n          <h3>First milestone</h3>\n          <p>Reached 1,000 users and launched our first major feature update.</p>\n        </div>\n      </div>\n      \n      <div class="timeline-item" role="listitem">\n        <div class="timeline-marker" aria-hidden="true"></div>\n        <div class="timeline-content">\n          <div class="timeline-date" aria-label="Year 2023">2023</div>\n          <h3>Expansion</h3>\n          <p>Grew the team to 20 people and opened our second office.</p>\n        </div>\n      </div>\n      \n      <div class="timeline-item" role="listitem">\n        <div class="timeline-marker" aria-hidden="true"></div>\n        <div class="timeline-content">\n          <div class="timeline-date" aria-label="Year 2025">2025</div>\n          <h3>Today</h3>\n          <p>Serving over 10,000 customers worldwide with innovative tools.</p>\n        </div>\n      </div>\n    </div>\n  </div>\n</section>\n\n<style>\n.timeline {\n  padding: 3rem 0;\n}\n\n.timeline h2 {\n  text-align: center;\n  margin-bottom: 3rem;\n}\n\n/* Timeline container with vertical line pseudo-element */\n.timeline-items {\n  max-width: 800px;\n  margin: 0 auto;\n  position: relative;\n}\n\n/* Vertical connecting line */\n.timeline-items::before {\n  content: "";\n  position: absolute;\n  left: 20px;\n  top: 0;\n  bottom: 0;\n  width: 2px;\n  background: #ddd;\n}\n\n/* Individual timeline entry */\n.timeline-item {\n  position: relative;\n  padding-left: 60px;\n  margin-bottom: 3rem;\n}\n\n/* Circle marker on the timeline */\n.timeline-marker {\n  position: absolute;\n  left: 11px;\n  top: 0;\n  width: 20px;\n  height: 20px;\n  border-radius: 50%;\n  background: #3498db;\n  border: 3px solid white;\n  box-shadow: 0 0 0 2px #3498db;\n}\n\n/* Content card for each timeline entry */\n.timeline-content {\n  background: white;\n  padding: 1.5rem;\n  border-radius: 8px;\n  box-shadow: 0 2px 8px rgba(0,0,0,0.1);\n}\n\n/* Date/year label */\n.timeline-date {\n  color: #3498db;\n  font-weight: 700;\n  font-size: 0.875rem;\n  text-transform: uppercase;\n  letter-spacing: 1px;\n  margin-bottom: 0.5rem;\n}\n\n.timeline-content h3 {\n  margin-bottom: 0.5rem;\n  color: #2c3e50;\n}\n\n.timeline-content p {\n  margin: 0;\n  color: #555;\n}\n\n/* Mobile adjustments - move line to left edge */\n@media (max-width: 768px) {\n  .timeline-items::before { left: 10px; }\n  .timeline-marker { left: 1px; }\n  .timeline-item { padding-left: 50px; }\n}\n</style>'
