                content: '/* === FOUNDATION: Same as "Complete Starter" === */\n\n/* === CSS Reset === */\n* {\n  margin: 0;\n  padding: 0;\n  box-sizing: border-box;\n}\n\n/* === CSS Variables === */\n:root {\n  --primary: #3498db;\n  --secondary: #2ecc71;\n  --accent: #e74c3c;\n  --dark: #2c3e50;\n  --light: #ecf0f1;\n  --border: #ddd;\n  --text: #333;\n  --text-muted: #6c757d;\n  --bg: #fff;\n  --radius: 8px;\n  --shadow: 0 2px 8px rgba(0,0,0,0.1);\n  --transition: 0.2s ease;\n  \n  /* Vertical rhythm - 8px base unit */\n  --space-xs: 0.5rem;   /* 8px */\n  --space-sm: 1rem;     /* 16px */\n  --space-md: 1.5rem;   /* 24px */\n  --space-lg: 2rem;     /* 32px */\n  --space-xl: 3rem;     /* 48px */\n  --space-2xl: 4rem;    /* 64px */\n}\n\n/* === Base Styles === */\nbody {\n  font-family: system-ui, -apple-system, sans-serif;\n  font-size: 1rem;\n  line-height: 1.6;\n  color: var(--text);\n  background: var(--bg);\n}\n\n/* === Typography === */\nh1, h2, h3, h4, h5, h6 {\n  margin-top: 0;\n  margin-bottom: var(--space-sm);\n  font-weight: 600;\n  line-height: 1.2;\n}\n\nh1 { \n  font-size: 2.5rem; \n  margin-bottom: var(--space-md);\n}\n\nh2 { \n  font-size: 2rem; \n  margin-bottom: var(--space-md);\n}\n\nh3 { \n  font-size: 1.5rem; \n}\n\nh4 { font-size: 1.25rem; }\nh5 { font-size: 1.125rem; }\nh6 { font-size: 1rem; }\n\np {\n  margin-bottom: var(--space-sm);\n}\n\np + p {\n  margin-top: var(--space-sm);\n}\n\na {\n  color: var(--primary);\n  text-decoration: none;\n  transition: color var(--transition);\n}\n\na:hover { color: var(--dark); }\n\n/* Lists */\nul, ol {\n  margin-bottom: var(--space-sm);\n  padding-left: 1.5em;\n}\n\nli {\n  margin-bottom: var(--space-xs);\n}\n\n/* Section spacing */\nsection {\n  margin-bottom: var(--space-xl);\n}\n\nsection + section {\n  margin-top: var(--space-xl);\n}\n\n/* === Layout === */\n.container {\n  max-width: 1200px;\n  margin: 0 auto;\n  padding: var(--space-xl) var(--space-sm);\n}\n\n.grid {\n  display: grid;\n  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));\n  gap: var(--space-lg);\n}\n\n.two-column {\n  display: grid;\n  grid-template-columns: 250px 1fr;\n  gap: var(--space-lg);\n}\n\n@media (max-width: 768px) {\n  .two-column {\n    grid-template-columns: 1fr;\n  }\n}\n\n/* === Components === */\n.card {\n  background: white;\n  border-radius: var(--radius);\n  padding: var(--space-md);\n  box-shadow: var(--shadow);\n  transition: transform var(--transition);\n  margin-bottom: var(--space-lg);\n}\n\n.card:hover {\n  transform: translateY(-4px);\n}\n\n.card > *:last-child {\n  margin-bottom: 0;\n}\n\nbutton,\n.btn {\n  display: inline-block;\n  padding: var(--space-xs) var(--space-md);\n  background: var(--primary);\n  color: white;\n  border: 2px solid var(--primary);\n  border-radius: var(--radius);\n  font-weight: 500;\n  font-size: 1rem;\n  font-family: inherit;\n  line-height: 1.5;\n  cursor: pointer;\n  transition: all var(--transition);\n}\n\nbutton:hover,\n.btn:hover {\n  background: var(--dark);\n  border-color: var(--dark);\n  transform: translateY(-2px);\n}\n\nbutton:disabled {\n  background: var(--light);\n  border-color: var(--border);\n  color: var(--text-muted);\n  cursor: not-allowed;\n  transform: none;\n  opacity: 0.6;\n}\n\nbutton[type=\"reset\"],\nbutton.btn-secondary,\n.btn-secondary {\n  background: var(--light);\n  color: var(--dark);\n  border-color: var(--border);\n}\n\nbutton[type=\"reset\"]:hover,\nbutton.btn-secondary:hover,\n.btn-secondary:hover {\n  background: var(--border);\n}\n\n/* === Forms === */\n.form-group {\n  margin-bottom: var(--space-md);\n}\n\nlabel {\n  display: block;\n  margin-bottom: var(--space-xs);\n  font-weight: 500;\n  line-height: 1.5;\n}\n\ninput,\ntextarea,\nselect {\n  width: 100%;\n  padding: var(--space-xs);\n  border: 2px solid var(--border);\n  border-radius: var(--radius);\n  font-size: 1rem;\n  font-family: inherit;\n  line-height: 1.5;\n  transition: border-color var(--transition);\n}\n\ninput:focus,\ntextarea:focus,\nselect:focus {\n  outline: none;\n  border-color: var(--primary);\n  box-shadow: 0 0 0 3px rgba(52, 152, 219, 0.1);\n}\n\ninput[type=\"range\"] {\n  width: 100%;\n  margin: var(--space-xs) 0;\n}\n\ninput[type=\"color\"] {\n  width: 100%;\n  height: 3rem;\n  border: 2px solid var(--border);\n  border-radius: var(--radius);\n  cursor: pointer;\n}\n\ninput[type=\"checkbox\"],\ninput[type=\"radio\"] {\n  margin-right: var(--space-xs);\n  cursor: pointer;\n}\n\ntextarea {\n  resize: vertical;\n  min-height: 100px;\n}\n\nselect[multiple] {\n  padding: 0;\n}\n\nselect[multiple] option {\n  padding: var(--space-xs);\n}\n\nfieldset {\n  border: 2px solid var(--border);\n  border-radius: var(--radius);\n  padding: var(--space-md);\n  margin-bottom: var(--space-md);\n}\n\nlegend {\n  padding: 0 var(--space-xs);\n  font-weight: 600;\n}\n\n/* === Sidebar Navigation === */\n.sidebar {\n  background: var(--light);\n  padding: var(--space-md);\n  border-radius: var(--radius);\n}\n\n.nav-link {\n  display: block;\n  padding: var(--space-xs) 0;\n  color: var(--dark);\n  border-bottom: 1px solid var(--border);\n  transition: all var(--transition);\n}\n\n.nav-link:hover {\n  color: var(--primary);\n  padding-left: var(--space-xs);\n}\n\n/* === Utilities === */\nimg { \n  max-width: 100%; \n  height: auto; \n  display: block;\n}\n\nhr { \n  border: 0; \n  border-top: 1px solid var(--border); \n  margin: var(--space-lg) 0; \n}\n\nblockquote {\n  margin: var(--space-lg) 0;\n  padding-left: var(--space-md);\n  border-left: 4px solid var(--primary);\n  font-style: italic;\n  color: var(--dark);\n}\n\ncode {\n  font-family: \'Courier New\', monospace;\n  font-size: 0.875em;\n  padding: 0.125em 0.25em;\n  background: var(--light);\n  border-radius: var(--radius);\n}\n\npre {\n  margin: var(--space-md) 0;\n  padding: var(--space-sm);\n  background: var(--light);\n  border-radius: var(--radius);\n  overflow-x: auto;\n}\n\npre code {\n  padding: 0;\n  background: none;\n}\n\n/* === ADDITIONAL STYLES FOR TESTING === */\n/* These styles extend the foundation above for comprehensive element testing */\n\n/* Text formatting elements */\nstrong, b { font-weight: 600; }\nem, i { font-style: italic; }\nu { text-decoration: underline; }\nmark { background: #fff3cd; padding: 0.125em 0.25em; }\nsmall { font-size: 0.875em; }\ndel, s { text-decoration: line-through; color: var(--text-muted); }\nins { text-decoration: none; border-bottom: 2px solid var(--secondary); }\nsub { vertical-align: sub; font-size: 0.75em; }\nsup { vertical-align: super; font-size: 0.75em; }\nabbr { cursor: help; border-bottom: 1px dotted var(--text-muted); }\ncite { font-style: italic; }\ndfn { font-style: italic; font-weight: 600; }\nvar { font-family: \'Courier New\', monospace; font-style: italic; }\nsamp { font-family: \'Courier New\', monospace; }\nq { font-style: italic; }\n\nkbd {\n  font-family: \'Courier New\', monospace;\n  font-size: 0.875em;\n  padding: 0.125em 0.375em;\n  background: var(--dark);\n  color: white;\n  border-radius: var(--radius);\n  box-shadow: 0 2px 0 rgba(0,0,0,0.2);\n}\n\n/* Enhanced blockquote for testing */\nblockquote {\n  margin: var(--space-md) 0;\n  padding: var(--space-sm) var(--space-md);\n  border-left: 4px solid var(--primary);\n  background: #f8f9fa;\n  font-style: italic;\n}\n\nblockquote footer {\n  margin-top: var(--space-xs);\n  font-style: normal;\n  font-size: 0.875em;\n  color: var(--text-muted);\n}\n\n/* Enhanced lists for testing */\nul ul, ol ol, ul ol, ol ul {\n  margin-top: var(--space-xs);\n  margin-bottom: var(--space-xs);\n}\n\ndl {\n  margin-bottom: var(--space-md);\n}\n\ndt {\n  font-weight: 600;\n  margin-top: var(--space-xs);\n}\n\ndd {\n  margin-left: 1.5em;\n  margin-bottom: var(--space-xs);\n}\n\n/* Tables */\ntable {\n  width: 100%;\n  margin-bottom: var(--space-md);\n  border-collapse: collapse;\n  border: 1px solid var(--border);\n}\n\ncaption {\n  padding: var(--space-xs);\n  font-weight: 600;\n  text-align: left;\n  caption-side: top;\n  color: var(--dark);\n}\n\nth, td {\n  padding: 0.75rem;\n  text-align: left;\n  border: 1px solid var(--border);\n}\n\nth {\n  background: var(--light);\n  font-weight: 600;\n  color: var(--dark);\n}\n\nthead th {\n  background: var(--dark);\n  color: white;\n}\n\ntbody tr:nth-child(even) {\n  background: #f8f9fa;\n}\n\ntbody tr:hover {\n  background: #e9ecef;\n}\n\ntfoot {\n  background: var(--light);\n  font-weight: 600;\n}\n\n/* Progress & Meter */\nprogress,\nmeter {\n  width: 200px;\n  height: 1.5rem;\n  vertical-align: middle;\n}\n\nprogress {\n  border: 1px solid var(--border);\n  border-radius: var(--radius);\n}\n\nprogress::-webkit-progress-bar {\n  background: var(--light);\n  border-radius: var(--radius);\n}\n\nprogress::-webkit-progress-value {\n  background: var(--primary);\n  border-radius: var(--radius);\n}\n\nprogress::-moz-progress-bar {\n  background: var(--primary);\n  border-radius: var(--radius);\n}\n\n/* Media elements */\nimg {\n  max-width: 100%;\n  height: auto;\n  display: block;\n  border-radius: var(--radius);\n}\n\nfigure {\n  margin: var(--space-md) 0;\n}\n\nfigcaption {\n  margin-top: var(--space-xs);\n  font-size: 0.875em;\n  color: var(--text-muted);\n  font-style: italic;\n}\n\n/* Semantic sections */\narticle {\n  margin-bottom: var(--space-md);\n  padding: var(--space-md);\n  background: #f8f9fa;\n  border-radius: var(--radius);\n  border: 1px solid var(--border);\n}\n\naside {\n  padding: var(--space-md);\n  background: #f8f9fa;\n  border-radius: var(--radius);\n  margin: var(--space-md) 0;\n}\n\naddress {\n  font-style: normal;\n  line-height: 1.8;\n  padding: var(--space-sm);\n  background: var(--light);\n  border-radius: var(--radius);\n}\n\nfooter {\n  margin-top: var(--space-2xl);\n  padding-top: var(--space-lg);\n  border-top: 1px solid var(--border);\n  text-align: center;\n  color: var(--text-muted);\n}\n\n/* Interactive elements */\ndetails {\n  margin-bottom: var(--space-sm);\n  padding: var(--space-sm);\n  border: 1px solid var(--border);\n  border-radius: var(--radius);\n}\n\nsummary {\n  cursor: pointer;\n  font-weight: 600;\n  padding: var(--space-xs);\n  user-select: none;\n  transition: background 0.2s;\n}\n\nsummary:hover {\n  background: var(--light);\n}\n\ndetails[open] summary {\n  margin-bottom: var(--space-sm);\n  border-bottom: 1px solid var(--border);\n}'
